  --popover-border: 220 15% 25%;

  --primary: 200 80% 50%;

  --primary-foreground: 0 0% 100%;

  --secondary: 220 12% 20%;

  --secondary-foreground: 220 10% 80%;

  --muted: 220 10% 15%;

