  --destructive: 0 70% 45%;

  --destructive-foreground: 0 0% 100%;

  --input: 220 15% 25%;
  --ring: 200 80% 60%;
  --chart-1: 200 80% 65%;
  --chart-2: 140 70% 60%;
  --chart-3: 280 60% 65%;
  --chart-4: 30 80% 65%;
  --chart-5: 15 75% 70%;

  --shadow-2xs: 0px 2px 0px 0px hsl(202.8169 89.1213% 53.1373% / 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(202.8169 89.1213% 53.1373% / 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(202.8169 89.1213% 53.1373% / 0.00), 0px 1px 2px -1px hsl(202.8169 89.1213% 53.1373% / 0.00);
  --shadow: 0px 2px 0px 0px hsl(202.8169 89.1213% 53.1373% / 0.00), 0px 1px 2px -1px hsl(202.8169 89.1213% 53.1373% / 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(202.8169 89.1213% 53.1373% / 0.00), 0px 2px 4px -1px hsl(202.8169 89.1213% 53.1373% / 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(202.8169 89.1213% 53.1373% / 0.00), 0px 4px 6px -1px hsl(202.8169 89.1213% 53.1373% / 0.00);
  --shadow-xl: 0px 2px 0px 0px hsl(202.8169 89.1213% 53.1373% / 0.00), 0px 8px 10px -1px hsl(202.8169 89.1213% 53.1373% / 0.00);
  --shadow-2xl: 0px 2px 0px 0px hsl(202.8169 89.1213% 53.1373% / 0.00);

}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/**
 * Using the elevate system.
 * Automatic contrast adjustment.
 *
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 *
 * // Using the tailwind utility when a data attribute is "on"
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 * // Or manually controlling the toggle state
 * <element className="toggle-elevate toggle-elevated" />
 *
 * Elevation systems have to handle many states.
 * - not-hovered, vs. hovered vs. active  (three mutually exclusive states)
 * - toggled or not
 * - focused or not (this is not handled with these utilities)
 *
 * Even without handling focused or not, this is six possible combinations that
 * need to be distinguished from eachother visually.
 */
@layer utilities {

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: -1;
    /* sits behind content but above backdrop */
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: 999;
    /* sits in front of content */
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }

  /* Mobile viewport and safe area handling for one screen fit */
  .mobile-viewport {
    height: calc(100dvh - env(safe-area-inset-top) - env(safe-area-inset-bottom));
    height: calc(100vh - env(safe-area-inset-top) - env(safe-area-inset-bottom)); /* fallback */

    /* Prevent height changes when keyboard opens */
    min-height: calc(100dvh - env(safe-area-inset-top) - env(safe-area-inset-bottom));
    min-height: calc(100vh - env(safe-area-inset-top) - env(safe-area-inset-bottom)); /* fallback */
  }

  /* Safe area utilities */
  .top-safe {
    padding-top: env(safe-area-inset-top);
  }

  .bottom-safe {
    padding-bottom: env(safe-area-inset-bottom);
  }

  .pb-safe {
    padding-bottom: calc(1rem + env(safe-area-inset-bottom));
  }

  .pt-safe {
    padding-top: calc(1rem + env(safe-area-inset-top));
  }

  .workout-mode-container {
    max-height: calc(100dvh - env(safe-area-inset-top) - env(safe-area-inset-bottom) - 4rem);
    max-height: calc(100vh - env(safe-area-inset-top) - env(safe-area-inset-bottom) - 4rem); /* fallback */
    overflow-y: auto;
    scroll-padding-top: 2rem;
    -webkit-overflow-scrolling: touch;

    /* Maintain consistent height even when keyboard is open */
    height: calc(100dvh - env(safe-area-inset-top) - env(safe-area-inset-bottom) - 4rem);
    height: calc(100vh - env(safe-area-inset-top) - env(safe-area-inset-bottom) - 4rem); /* fallback */
  }

  .set-list-container {
    max-height: calc(35dvh - env(safe-area-inset-bottom));
    max-height: calc(35vh - env(safe-area-inset-bottom)); /* fallback */
    overflow-y: auto;
    overflow-x: hidden;
    scroll-padding-top: 1rem;
    -webkit-overflow-scrolling: touch;

    /* Stable height for keyboard interactions */
    min-height: 8rem;

    /* Improved scrolling on mobile */
    scrollbar-width: none;
    -ms-overflow-style: none;
  }

  .set-list-container::-webkit-scrollbar {
    display: none;
  }

  .fixed-row-height {
    height: 4rem; /* 64px fixed height to prevent layout shifts */
    min-height: 4rem;
  }

  /* Scroll margin for SetRow elements to account for keyboard and header */
  .scroll-margin-top {
    scroll-margin-top: 6rem; /* Adjust for header and keyboard */
  }

  /* iOS safe area support */
  @supports (padding: env(safe-area-inset-top)) {
    .mobile-viewport {
      height: calc(100dvh - env(safe-area-inset-top) - env(safe-area-inset-bottom));
    }

    .workout-mode-container {
      max-height: calc(100dvh - env(safe-area-inset-top) - env(safe-area-inset-bottom) - 4rem);
    }

    .set-list-container {
      max-height: calc(35dvh - env(safe-area-inset-bottom));
    }
  }

  /* iOS keyboard handling */
  @supports (-webkit-touch-callout: none) {
    /* Prevent viewport scaling on iOS when keyboard opens */
    .workout-mode-container {
      position: relative;
      overflow-y: auto;
      height: 100%;
      -webkit-overflow-scrolling: touch;
    }

    /* Ensure set list container maintains proper scrolling */
    .set-list-container {
      position: relative;
      -webkit-overflow-scrolling: touch;
      scroll-padding-top: 4rem;
    }

    /* Prevent zoom on input focus */
    input[type="number"], input[type="text"], input[type="email"], input[type="tel"] {
      font-size: 16px !important;
      transform: translateZ(0);
      -webkit-transform: translateZ(0);
    }
  }

  /* Keyboard-aware scrolling behavior */
  .keyboard-open {
    /* Applied to body when keyboard is detected */
    position: fixed;
    width: 100%;
    height: 100vh;
    overflow: hidden;
  }

  /* Smooth scrolling for row visibility adjustments */
  .smooth-scroll {
    scroll-behavior: smooth;
  }

  /* Prevent layout shifts during keyboard interactions */
  .stable-layout {
    contain: layout style;
  }

  /* Enhanced mobile input handling */
  @media screen and (-webkit-min-device-pixel-ratio: 0) {
    input[type="number"] {
      font-size: 16px;
      /* Prevent iOS zoom while maintaining design */
      -webkit-appearance: none;
      border-radius: 0;
    }
  }
}

