import { useState, useRef } from "react";
import { cn } from "@/lib/utils";
import GlassCard from "./GlassCard";
import { Button } from "@/components/ui/button";
import { Upload, FileText, CheckCircle, AlertCircle } from "lucide-react";

interface CSVUploadProps {
  onFileSelect?: (file: File) => void;
  onParse?: (data: any[]) => void;
  isProcessing?: boolean;
  className?: string;
}

export default function CSVUpload({ 
  onFileSelect, 
  onParse, 
  isProcessing = false,
  className 
}: CSVUploadProps) {
  const [dragOver, setDragOver] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [uploadStatus, setUploadStatus] = useState<'idle' | 'success' | 'error'>('idle');
  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setDragOver(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setDragOver(false);

    const files = e.dataTransfer.files;
    if (files.length > 0) {
      handleFileSelection(files[0]);
    }
  };

  const handleFileSelection = (file: File) => {
    if (file.type !== 'text/csv' && !file.name.endsWith('.csv')) {
      setUploadStatus('error');
      console.log('Invalid file type. Please select a CSV file.');
      return;
    }

    setSelectedFile(file);
    setUploadStatus('success');
    onFileSelect?.(file);
    console.log(`CSV file selected: ${file.name} (${(file.size / 1024).toFixed(1)}KB)`);
  };

  const handleFileInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      handleFileSelection(files[0]);
    }
  };

  const handleBrowseClick = () => {
    fileInputRef.current?.click();
  };

  const handleParseClick = () => {
    if (selectedFile) {
      console.log('Parsing CSV file:', selectedFile.name);
      // Mock parsing simulation
      setTimeout(() => {
        onParse?.([{ mock: 'data' }]);
      }, 1000);
    }
  };

  return (
    <GlassCard 
      variant="secondary" 
      className={cn("transition-all duration-300", className)}
    >
      <div className="p-6">
        <div className="text-center mb-6">
          <FileText className="w-12 h-12 text-primary mx-auto mb-3" />
          <h3 className="text-lg font-semibold text-white mb-2">
            Import Workout Data
          </h3>
          <p className="text-white/75 text-sm">
            Upload your "TREINO SETEMBRO .csv" file to parse workout sessions
          </p>
        </div>

        {/* File Drop Zone */}
        <div
          className={cn(
            "border-2 border-dashed rounded-xl p-8 text-center transition-all duration-300",
            dragOver 
              ? "border-primary bg-primary/10" 
              : "border-white/30 hover:border-white/50",
            uploadStatus === 'success' && "border-green-400 bg-green-400/10",
            uploadStatus === 'error' && "border-red-400 bg-red-400/10"
          )}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={handleDrop}
          data-testid="csv-drop-zone"
        >
          <input
            ref={fileInputRef}
            type="file"
            accept=".csv"
            onChange={handleFileInputChange}
            className="hidden"
            data-testid="csv-file-input"
          />

          {!selectedFile ? (
            <div className="space-y-4">
              <Upload className={cn(
                "w-10 h-10 mx-auto transition-colors",
                dragOver ? "text-primary" : "text-white/60"
              )} />
              <div>
                <p className="text-white font-medium mb-1">
                  Drop your CSV file here
                </p>
                <p className="text-white/60 text-sm">
                  or{" "}
                  <button
                    onClick={handleBrowseClick}
                    className="text-primary hover:underline font-medium"
                    data-testid="browse-button"
                  >
                    browse files
                  </button>
                </p>
              </div>
            </div>
          ) : (
            <div className="space-y-3">
              {uploadStatus === 'success' ? (
                <CheckCircle className="w-10 h-10 text-green-400 mx-auto" />
              ) : (
                <AlertCircle className="w-10 h-10 text-red-400 mx-auto" />
              )}
              <div>
                <p className="text-white font-medium" data-testid="selected-file-name">
                  {selectedFile.name}
                </p>
                <p className="text-white/60 text-sm">
                  {(selectedFile.size / 1024).toFixed(1)}KB
                </p>
              </div>
            </div>
          )}
        </div>

        {/* Actions */}
        {selectedFile && uploadStatus === 'success' && (
          <div className="mt-6 flex gap-3">
            <Button
              onClick={handleParseClick}
              disabled={isProcessing}
              className="flex-1"
              data-testid="parse-button"
            >
              {isProcessing ? "Parsing..." : "Parse Workout Data"}
            </Button>
            <Button
              variant="outline"
              onClick={() => {
                setSelectedFile(null);
                setUploadStatus('idle');
                if (fileInputRef.current) {
                  fileInputRef.current.value = '';
                }
              }}
              data-testid="reset-button"
            >
              Reset
            </Button>
          </div>
        )}
      </div>
    </GlassCard>
  );
}

client/src/components/ExerciseCard.tsx.content:
import { useState } from "react";
import { cn } from "@/lib/utils";
import GlassCard from "./GlassCard";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Dumbbell, Clock, Target, Info } from "lucide-react";

export interface Exercise {
  id: string;
  name: string;
  sets: number;
  repsMin?: number;
  repsMax?: number;
  timeSecondsMin?: number;
  timeSecondsMax?: number;
  stepsCount?: number;
  unit: 'reps' | 'seconds' | 'steps';
  perSide: boolean;
  weight?: number;
  notes?: string;
  formGuidance?: string;
  muscleGroup: string;
  mainMuscle: string;
}

interface ExerciseCardProps {
  exercise: Exercise;
  isCompleted?: boolean;
  onComplete?: (exerciseId: string) => void;
}

export default function ExerciseCard({ 
  exercise, 
  isCompleted = false, 
  onComplete 
}: ExerciseCardProps) {
  const [completed, setCompleted] = useState(isCompleted);
  const [showDetails, setShowDetails] = useState(false);

  const handleComplete = () => {
    const newCompleted = !completed;
    setCompleted(newCompleted);
    onComplete?.(exercise.id);
    console.log(`Exercise ${exercise.name} ${newCompleted ? 'completed' : 'uncompleted'}`);
  };

  const formatPrescription = () => {
    const { unit, repsMin, repsMax, timeSecondsMin, timeSecondsMax, stepsCount, perSide } = exercise;

    let prescription = '';

    if (unit === 'reps') {
      if (repsMin === repsMax) {
        prescription = `${repsMin} reps`;
      } else {
        prescription = `${repsMin}-${repsMax} reps`;
      }
    } else if (unit === 'seconds') {
      if (timeSecondsMin === timeSecondsMax) {
        prescription = `${timeSecondsMin}s`;
      } else {
        prescription = `${timeSecondsMin}-${timeSecondsMax}s`;
      }
    } else if (unit === 'steps') {
      prescription = `${stepsCount} steps`;
    }

    return perSide ? `${prescription} per side` : prescription;
  };

  return (
    <GlassCard 
      variant="primary" 
      className={cn(
        "transition-all duration-300",
        completed && "opacity-75 bg-green-500/20 dark:bg-green-400/20"
      )}
    >
      <div className="p-6 space-y-4">
        {/* Header */}
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <h3 className="text-lg font-semibold text-white mb-1" data-testid={`exercise-name-${exercise.id}`}>
              {exercise.name}
            </h3>
            <div className="flex flex-wrap gap-2 mb-2">
              <Badge variant="secondary" className="text-xs">
                {exercise.mainMuscle}
              </Badge>
              <Badge variant="outline" className="text-xs text-white/80 border-white/30">
                {exercise.muscleGroup}
              </Badge>
            </div>
          </div>
          <Button
            size="sm"
            variant={completed ? "default" : "outline"}
            onClick={handleComplete}
            className="flex-shrink-0"
            data-testid={`complete-button-${exercise.id}`}
          >
            {completed ? "Done" : "Mark Complete"}
          </Button>
        </div>

        {/* Exercise Details */}
        <div className="grid grid-cols-2 gap-4 text-white">
          <div className="flex items-center gap-2">
            <Target className="w-4 h-4 text-primary" />
            <div>
              <div className="text-sm opacity-75">Sets</div>
              <div className="font-mono font-semibold" data-testid={`sets-${exercise.id}`}>
                {exercise.sets}
              </div>
            </div>
          </div>

          <div className="flex items-center gap-2">
            {exercise.unit === 'seconds' ? (
              <Clock className="w-4 h-4 text-primary" />
            ) : (
              <Dumbbell className="w-4 h-4 text-primary" />
            )}
            <div>
              <div className="text-sm opacity-75">
                {exercise.unit === 'seconds' ? 'Time' : 'Reps'}
              </div>
              <div className="font-mono font-semibold" data-testid={`prescription-${exercise.id}`}>
                {formatPrescription()}
              </div>
            </div>
          </div>
        </div>

        {/* Weight */}
        {exercise.weight && (
          <div className="flex items-center gap-2 text-white">
            <Dumbbell className="w-4 h-4 text-primary" />
            <div>
              <span className="text-sm opacity-75">Weight: </span>
              <span className="font-mono font-semibold" data-testid={`weight-${exercise.id}`}>
                {exercise.weight}kg
              </span>
            </div>
          </div>
        )}

        {/* Notes and Form Guidance Toggle */}
        {(exercise.notes || exercise.formGuidance) && (
          <div>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setShowDetails(!showDetails)}
