                </Link>
                <Link href="/import">
                  <Button 
                    variant="ghost" 
                    className="w-full justify-start text-white hover:text-white"
                    onClick={() => setShowMenu(false)}
                    data-testid="nav-import"
                  >
                    <Upload className="w-4 h-4 mr-2" />
