                </Link>
                <Link href="/">
                  <Button 
                    variant="ghost" 
                    className="w-full justify-start text-white hover:text-white"
                    onClick={() => setShowMenu(false)}
                    data-testid="nav-calendar"
                  >
                    <Calendar className="w-4 h-4 mr-2" />
